#!/bin/bash
# Findet fehlgeschlagene Executions eines Workflows

# Konfiguration
N8N_URL="https://n8n.srv1098810.hstgr.cloud"
API_KEY="${N8N_API_KEY:-}"  # Kann als Umgebungsvariable gesetzt werden

# Farben für Output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Prüfe ob Workflow-ID übergeben wurde
if [ -z "$1" ]; then
    echo -e "${RED}Fehler: Keine Workflow-ID angegeben${NC}"
    echo ""
    echo "Usage: $0 <workflow-id> [limit] [api-key]"
    echo ""
    echo "Beispiel:"
    echo "  $0 12345"
    echo "  $0 12345 20"
    echo "  N8N_API_KEY=dein-key $0 12345"
    exit 1
fi

WORKFLOW_ID="$1"
LIMIT="${2:-10}"  # Standard: 10 Executions

# API-Key prüfen
if [ -z "$API_KEY" ] && [ -n "$3" ]; then
    API_KEY="$3"
fi

if [ -z "$API_KEY" ]; then
    echo -e "${YELLOW}Warnung: Kein API-Key gesetzt${NC}"
    echo "Setze N8N_API_KEY als Umgebungsvariable oder gib ihn als dritten Parameter an"
    echo ""
    read -p "API-Key eingeben (oder Enter zum Abbrechen): " API_KEY
    if [ -z "$API_KEY" ]; then
        echo "Abgebrochen."
        exit 1
    fi
fi

echo -e "${BLUE}🔍 Suche fehlgeschlagene Executions...${NC}"
echo "Workflow ID: $WORKFLOW_ID"
echo "Limit: $LIMIT"
echo ""

# Hole fehlgeschlagene Executions
RESPONSE=$(curl -s -X GET \
  "$N8N_URL/api/v1/executions?workflowId=$WORKFLOW_ID&status=error&limit=$LIMIT" \
  -H "X-N8N-API-KEY: $API_KEY" \
  -H "Content-Type: application/json")

HTTP_CODE=$(curl -s -w "%{http_code}" -o /dev/null \
  -X GET \
  "$N8N_URL/api/v1/executions?workflowId=$WORKFLOW_ID&status=error&limit=$LIMIT" \
  -H "X-N8N-API-KEY: $API_KEY" \
  -H "Content-Type: application/json")

if [ "$HTTP_CODE" != "200" ]; then
    echo -e "${RED}Fehler: HTTP Status Code $HTTP_CODE${NC}"
    if [ "$HTTP_CODE" == "401" ]; then
        echo -e "${YELLOW}Authentifizierung fehlgeschlagen. Bitte API-Key prüfen.${NC}"
    elif [ "$HTTP_CODE" == "404" ]; then
        echo -e "${YELLOW}Workflow nicht gefunden. Bitte Workflow-ID prüfen.${NC}"
    fi
    exit 1
fi

# Prüfe ob jq installiert ist
if ! command -v jq &> /dev/null; then
    echo -e "${YELLOW}Warnung: jq nicht installiert. Installiere es für bessere Formatierung.${NC}"
    echo "$RESPONSE" > failed-executions-raw.json
    echo "Rohdaten gespeichert in: failed-executions-raw.json"
    exit 0
fi

# Parse und zeige Ergebnisse
EXECUTION_COUNT=$(echo "$RESPONSE" | jq '.data | length')

if [ "$EXECUTION_COUNT" -eq 0 ]; then
    echo -e "${GREEN}✅ Keine fehlgeschlagenen Executions gefunden!${NC}"
    exit 0
fi

echo -e "${RED}❌ Gefunden: $EXECUTION_COUNT fehlgeschlagene Execution(s)${NC}"
echo ""

# Speichere in Datei
OUTPUT_FILE="failed-executions-${WORKFLOW_ID}.json"
echo "$RESPONSE" | jq '.' > "$OUTPUT_FILE"

# Zeige Zusammenfassung
