// Funktion zum „JSON-sicher“ machen eines Strings
function sanitizeForJson(s) {
  if (typeof s !== 'string') {
    s = String(s ?? '');
  }

  // C0-Steuerzeichen entfernen, aber \n und \t erlauben
  // (0x00–0x1F & 0x7F), außer \n (0x0A) und \t (0x09)
  s = s.replace(/[\u0000-\u0008\u000B\u000C\u000E-\u001F\u007F]/g, '');

  // Unicode Line/Paragraph Separator → normale Zeilenumbrüche
  s = s.replace(/\u2028|\u2029/g, '\n');

  // Unpaare Surrogate ersetzen (können JSON/UTF-8 killen)
  s = s.replace(/[\uD800-\uDBFF](?![\uDC00-\uDFFF])|(?<![\uD800-\uDBFF])[\uDC00-\uDFFF]/g, '�');

  // Doppelte Leerzeichen rund um Zeilenumbrüche entschärfen
  s = s.replace(/[ \t]+\n/g, '\n');

  return s;
}

// 1) Text aus vorherigem Node holen
const raw = $input.first().json;
// kompletter JSON-Output des AI-Nodes

// 2) Text extrahieren
const rawText = raw.content[0].text;

// 3) Sanitizen
const inputText = sanitizeForJson(rawText);

// 4) Ergebnis zurückgeben
return [
  {
    json: {
      text: JSON.stringify(inputText),
    },
  },
];
