const workflows = $json.data || [];
return {
  workflows: workflows,
  index: 0,
  anzahl: workflows.length,
  neu: 0,
  updates: 0,
  geloescht: 0,
  skip: 0,
  tabelle: []
};

const r = $json; // Rucksack
const wf = r.workflows[r.index];

const workflowId = wf.id || '';
const workflowName = wf.name || 'Unnamed Workflow';
const cleanName = String(workflowName).replace(/[^a-zA-Z0-9-_√§√∂√º√Ñ√ñ√ú√ü ]/g, '').replace(/\s+/g, '-').toLowerCase();

// Neue Struktur: n8n/${workflowId}/${cleanName}.json
const dateipfad = `n8n/${workflowId}/${cleanName}.json`;
const workflowContent = Buffer.from(JSON.stringify(wf, null, 2)).toString('base64');

// Archiv-Logik: l√∂schen statt sichern?
let actionOverride = null;
if (wf.isArchived === true) {
  actionOverride = 'delete';
}

return {
  ...r,
  workflowId: workflowId,
  workflowName: workflowName,
  workflowData: wf,
  dateipfad: dateipfad,
  workflowContent: workflowContent,
  updatedAt: wf.updatedAt || wf.createdAt || new Date().toISOString(),
  actionOverride: actionOverride,
  skipIteration: false
};

const r = $node["Extrahiere Workflow"].json;
const githubFile = $node["Pr√ºfe GitHub Datei"].json;

let action = 'skip';
let sha = null;

// Falls im vorherigen Node bereits 'delete' bestimmt wurde (weil archiviert)
if (r.actionOverride === 'delete') {
  if (githubFile && !githubFile.error && githubFile.sha) {
    action = 'delete';
    sha = githubFile.sha;
  } else {
    action = 'skip'; // Archiviert but not on GitHub
  }
} else if (githubFile && !githubFile.error && githubFile.sha) {
  sha = githubFile.sha;
  const remoteContent = (githubFile.content || '').replace(/\s/g, '');
  const localContent = (r.workflowContent || '').replace(/\s/g, '');
  if (remoteContent !== localContent) {
    action = 'update';
  }
} else {
  action = 'create';
}

return {
  ...r,
  action: action,
  sha: sha
};

// Pr√ºfe ob erfolgreich gespeichert und ob alte Dateien gel√∂scht werden m√ºssen
// Hole Daten aus beiden vorherigen Nodes
const r = $node["Bestimme Handlung"].json;
const githubResponse = $json;

// Stelle sicher, dass alle ben√∂tigten Felder vorhanden sind
if (!r.tabelle) r.tabelle = [];
if (r.neu === undefined) r.neu = 0;
if (r.updates === undefined) r.updates = 0;
if (r.skip === undefined) r.skip = 0;

// Nur wenn erfolgreich UND create-Action, m√ºssen wir alte Dateien pr√ºfen
if (!githubResponse.error && r.action === 'create' && r.workflowId) {
  // Bereite Ordner-Pfad vor
  const ordnerPfad = `n8n/${r.workflowId}`;
  const aktuelleDatei = r.dateipfad.split('/').pop();

  return {
    ...r,
    githubResponse: githubResponse,
    ordnerPfad: ordnerPfad,
    aktuelleDatei: aktuelleDatei,
    mussPruefen: true,
    alteDateien: r.alteDateien || []
  };
}

// Keine Pr√ºfung n√∂tig - direkt zur Statistik
return {
  ...r,
  githubResponse: githubResponse,
  mussPruefen: false,
  alteDateien: r.alteDateien || []
};

// Finde alte Dateien im Ordner und bereite sie zum L√∂schen vor
const r = $json;
const ordnerInhaltNode = $node["Liste Ordner"];

// Wenn Node nicht ausgef√ºhrt wurde oder Fehler, √ºberspringe
if (!ordnerInhaltNode || !ordnerInhaltNode.json) {
  return {
    ...r,
    alteDateien: []
  };
}

const ordnerInhalt = ordnerInhaltNode.json;

// GitHub API gibt bei Ordner-Liste ein Array zur√ºck
// Wenn Fehler oder kein Array, √ºberspringe
if (ordnerInhalt.error || !Array.isArray(ordnerInhalt)) {
  return {
    ...r,
    alteDateien: []
  };
}

// Finde alle JSON-Dateien au√üer der aktuellen
const alteDateien = ordnerInhalt
  .filter(file => file.type === 'file' && file.name && file.name.endsWith('.json') && file.name !== r.aktuelleDatei)
  .map(file => ({
    path: file.path,
    name: file.name,
    sha: file.sha
  }));

return {
  ...r,
  alteDateien: alteDateien
};

// Erstelle L√∂sch-Items f√ºr jede alte Datei
const r = $json;
const alteDateien = r.alteDateien || [];

return alteDateien.map(datei => ({
  ...r,
  zuLoeschendeDatei: datei.path,
  zuLoeschendeSha: datei.sha,
  zuLoeschenderName: datei.name
}));

// Aktualisiere Statistiken
// Hole Rucksack von Pr√ºfe alte Dateien
let r;
try {
  r = $node["Pr√ºfe alte Dateien"].json;
} catch (e) {
  // Fallback: Hole von Bestimme Handlung
  try {
    r = $node["Bestimme Handlung"].json;
  } catch (e2) {
    r = {};
  }
}

// Stelle sicher, dass r ein Objekt ist
if (!r || typeof r !== 'object') {
  r = {};
}

const githubResponse = r.githubResponse || {};

// Stelle sicher, dass alle Felder vorhanden sind
if (!Array.isArray(r.tabelle)) r.tabelle = [];
if (typeof r.neu !== 'number') r.neu = 0;
if (typeof r.updates !== 'number') r.updates = 0;
if (typeof r.skip !== 'number') r.skip = 0;
if (typeof r.index !== 'number') r.index = 0;
if (!Array.isArray(r.alteDateien)) r.alteDateien = [];

if (r.action === 'create') {
  r.neu++;
} else if (r.action === 'update') {
  r.updates++;
} else {
  r.skip++;
}

// F√ºge zur Tabelle hinzu
if (r.action !== 'skip') {
  const status = githubResponse.error ? '‚ùå Fehler' : '‚úÖ Erfolg';
  const alteDateienInfo = r.alteDateien && r.alteDateien.length > 0 ? ` (${r.alteDateien.length} alte Datei${r.alteDateien.length > 1 ? 'en' : ''} gel√∂scht)` : '';
  r.tabelle.push(`${status} ${r.action === 'create' ? 'NEU' : 'UPDATE'}: ${r.workflowName || 'Unbekannt'}${alteDateienInfo}`);
}

// Z√§hler erh√∂hen
r.index++;

return r;

// Wenn skip: Aktualisiere Statistik direkt
