const r = $json; // Rucksack
const wf = r.workflows[r.index];

// Default values
let skipIteration = false;

// Ãœberspringe archivierte Workflows
if (wf.isArchived === true) {
  skipIteration = true;
}

if (skipIteration) {
    return { ...r, skipIteration: true };
}

const workflowId = wf.id || '';
const workflowName = wf.name || 'Unnamed Workflow';
const cleanName = String(workflowName).replace(/[^a-zA-Z0-9-_Ã¤Ã¶Ã¼Ã„Ã–ÃœÃŸ ]/g, '').replace(/\s+/g, '-').toLowerCase();

// Neue Struktur: n8n/${workflowId}/${cleanName}.json
const dateipfad = `n8n/${workflowId}/${cleanName}.json`;
const workflowContent = Buffer.from(JSON.stringify(wf, null, 2)).toString('base64');

return {
  ...r,
  workflowId: workflowId,
  workflowName: workflowName,
  workflowData: wf,
  dateipfad: dateipfad,
  workflowContent: workflowContent,
  updatedAt: wf.updatedAt || wf.createdAt || new Date().toISOString(),
  skipIteration: false
};

const r = $json; // Rucksack
const githubFile = $node["PrÃ¼fe GitHub Datei"].json;

let action = 'skip';
let sha = null;
let alteDateiZuLoeschen = null;
let debugInfo = {};

// Debug: Log GitHub File Status
debugInfo.githubFileExists = !!githubFile;
debugInfo.hasError = !!(githubFile && githubFile.error);
debugInfo.hasSha = !!(githubFile && githubFile.sha);
debugInfo.errorMessage = githubFile && githubFile.error ? (githubFile.error.message || JSON.stringify(githubFile.error)) : null;

// PrÃ¼fe ob Datei auf GitHub existiert
if (githubFile && !githubFile.error && githubFile.sha) {
  sha = githubFile.sha;

  // Vergleich: Inhalt geÃ¤ndert?
  const remoteContent = (githubFile.content || '').replace(/\s/g, '');
  const localContent = (r.workflowContent || '').replace(/\s/g, '');

  debugInfo.contentMatch = remoteContent === localContent;

  if (remoteContent !== localContent) {
    action = 'update';
  }
} else {
  // Datei existiert nicht - kÃ¶nnte umbenannt worden sein
  // PrÃ¼fe ob Ordner existiert und andere Dateien enthÃ¤lt
  action = 'create';
  debugInfo.reason = 'Datei existiert nicht in neuer Struktur';
}

debugInfo.action = action;
debugInfo.dateipfad = r.dateipfad;
debugInfo.workflowId = r.workflowId;
debugInfo.workflowName = r.workflowName;

return {
  ...r,
  action: action,
  sha: sha,
  alteDateiZuLoeschen: alteDateiZuLoeschen,
  debugInfo: debugInfo
};

