if (r.updates === undefined) r.updates = 0;
if (r.skip === undefined) r.skip = 0;

// Nur wenn erfolgreich UND create-Action, mÃ¼ssen wir alte Dateien prÃ¼fen
if (!githubResponse.error && r.action === 'create' && r.workflowId) {
  // Bereite Ordner-Pfad vor
  const ordnerPfad = `n8n/${r.workflowId}`;
  const aktuelleDatei = r.dateipfad.split('/').pop();

  return {
    ...r,
    githubResponse: githubResponse,
    ordnerPfad: ordnerPfad,
    aktuelleDatei: aktuelleDatei,
    mussPruefen: true,
    alteDateien: r.alteDateien || []
  };
}

// Keine PrÃ¼fung nÃ¶tig - direkt zur Statistik
return {
