// Konfiguration: Preise für Claude Sonnet 4
const COST_INPUT_PER_M = 0.003;
const COST_OUTPUT_PER_M = 0.015;

// 1. Daten holen
const llmRawOutput = $json.output;
const splitBatchesData = $('2. Split In Batches (Prototyp)').first().json;
const investorId = splitBatchesData.row_number || 1;
const investorName = splitBatchesData['Investor Name'] || splitBatchesData.Investor || 'unknown';

// Token-Schätzung (falls usage fehlt)
const llmOutput = $json.output || '';
const inputData = JSON.stringify(splitBatchesData);
const estimatedInputTokens = Math.ceil(inputData.length / 4);
const estimatedOutputTokens = Math.ceil(llmOutput.length / 4);

const inputTokens = $json.usage?.input_tokens || estimatedInputTokens;
const outputTokens = $json.usage?.output_tokens || estimatedOutputTokens;

// 2. Kosten berechnen
const costPerRun = (
    (inputTokens / 1000000) * COST_INPUT_PER_M) +
    ((outputTokens / 1000000) * COST_OUTPUT_PER_M
);

const costUsd = parseFloat(costPerRun.toFixed(6));

// 3. Markdown-Tabelle parsen
const lines = llmRawOutput.split('\n').filter(line => line.trim().length > 0);

let tableStartIndex = -1;
for (let i = 0; i < lines.length; i++) {
    if (lines[i].includes('|') && lines[i].includes('Investor')) {
        tableStartIndex = i;
        break;
    }
}

if (tableStartIndex === -1 || lines.length < tableStartIndex + 3) {
    return [{ 
        error: 'LLM Output enthält keine gültige Tabelle', 
        raw_output: llmRawOutput,
        investor_id: investorId,
        investor_name: investorName
    }];
}

const dataLines = lines.slice(tableStartIndex + 2); 
const parsedResults = [];

for (const line of dataLines) {
    if (!line.includes('|')) continue;

    const columns = line.split('|').slice(1, -1).map(col => col.trim()); 

    if (columns.length === 7) {
        let score_numeric = null;
        let source_factor_numeric = null;

        if (columns[2].length > 0) {
            score_numeric = parseFloat(columns[2].replace(',', '.'));
        }
        if (columns[5].length > 0) {
            source_factor_numeric = parseFloat(columns[5].replace(',', '.'));
        }

        parsedResults.push({
            investor_id: investorId,
            investor_name: investorName,
            llm_cost_usd: costUsd,
            raw_markdown_output: llmRawOutput,
            criterion_name: columns[1],
            score: score_numeric,
            source_type: columns[3],
            source_year: parseInt(columns[4], 10) || null,
            source_factor: source_factor_numeric,
            notes: columns[6]
        });
    }
}

return parsedResults;

// Node 6: Daten für Excel vorbereiten (mit EUR-Konvertierung)

// EUR/USD Wechselkurs (aktuell ca. 1.10, regelmäßig aktualisieren!)
const EUR_USD_RATE = 1.10; // 1 USD = 0.91 EUR bei Rate 1.10

const items = $input.all();

// Prüfe ob Daten vorhanden sind
if (!items || items.length === 0) {
  return [];
}

// Gruppiere nach Investor
const investorGroups = {};

for (const item of items) {
  const investorId = item.json.investor_id;
  const investorName = item.json.investor_name;

  if (!investorGroups[investorId]) {
    investorGroups[investorId] = {
      investor_id: investorId,
      investor_name: investorName,
      batch_id: $execution.id, // Execution ID als Batch ID
      batch_number: 1, // TODO: Dynamisch aus Split In Batches holen
      llm_cost_usd: item.json.llm_cost_usd || 0,
      criteria: [],
      total_score: 0,
      count_scores: 0
    };
  }

  // Sammle Kriterien
  investorGroups[investorId].criteria.push({
    name: item.json.criterion_name,
    score: item.json.score,
    source_type: item.json.source_type,
    source_year: item.json.source_year,
    source_factor: item.json.source_factor,
    notes: item.json.notes
  });

  // Summiere Scores (nur wenn nicht null)
  if (item.json.score !== null && item.json.score !== undefined) {
    investorGroups[investorId].total_score += item.json.score;
    investorGroups[investorId].count_scores += 1;
  }
}

// Erstelle Output für Google Sheet
const output = [];

Object.values(investorGroups).forEach(group => {
  // Berechne Durchschnitts-Score (FIX: als Dezimalzahl!)
  const avgScore = group.count_scores > 0 
    ? parseFloat((group.total_score / group.count_scores).toFixed(3))
    : null;

  // Kosten in EUR umrechnen
  const costEUR = group.llm_cost_usd > 0 
    ? parseFloat((group.llm_cost_usd / EUR_USD_RATE).toFixed(6))
    : 0;

  // Erstelle eine Zeile pro Kriterium (für detailliertes Sheet)
  group.criteria.forEach(criterion => {
    output.push({
      'Batch ID': group.batch_id,
      'Batch Number': group.batch_number,
      'Investor ID': group.investor_id,
      'Investor Name': group.investor_name,
      'Criterion': criterion.name,
      'Score_0_to_1': criterion.score !== null ? criterion.score : '',
      'Source_Type': criterion.source_type || '',
      'Source_Year': criterion.source_year || '',
      'Source_Factor': criterion.source_factor || '',      
      'Notes': criterion.notes || '',
      'Average Score': avgScore, // FIX: Jetzt korrekt als 0.6 statt 600
      'Cost USD': parseFloat(group.llm_cost_usd.toFixed(6)),
      'Cost EUR': costEUR, // NEU: Kosten in EUR
      'Timestamp': new Date().toISOString()
    });
  });
});

return output;

