// Multi-Search Tavily Research
const TAVILY_API_KEY = "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"; // Ersetze mit deinem Key!

const investorName = $json['Investor Name'];

const searches = [
  investorName + ' climate tech investments',
  investorName + ' CDR carbon removal',
  investorName + ' portfolio companies',
  investorName + ' funding series A',
  investorName + ' investment thesis'
];

const searchResults = [];

for (let i = 0; i < searches.length; i++) {
  try {
    const result = await this.helpers.httpRequest({
      method: 'POST',
      url: 'https://api.tavily.com/search',
      body: {
        api_key: TAVILY_API_KEY,
        query: searches[i],
        search_depth: 'advanced',
        max_results: 3
      },
      json: true
    });

    searchResults.push({
      query: searches[i],
      answer: result.answer || '',
      results: result.results || []
    });

  } catch (error) {
    searchResults.push({
      query: searches[i],
      answer: 'Fehler',
      results: []
    });
  }
}

return {
  'Investor Name': investorName,
  'URL': $json.URL,
  'Investor Type': $json['Investor Type'],
  'research_data': {
    searches_performed: searchResults.length,
    search_results: searchResults
  }
};

const researchData = $json.research_data;
const searchResults = researchData.search_results;

// Sammle die wichtigsten Insights aus allen Suchen
let formattedResearch = '';

for (let i = 0; i < searchResults.length; i++) {
  const search = searchResults[i];

  formattedResearch += '\n\n### Suche ' + (i + 1) + ': ' + search.query + '\n';

  if (search.answer && search.answer.length > 0) {
    formattedResearch += 'Antwort: ' + search.answer + '\n\n';
  }

  // Top 3 Ergebnisse
  const results = search.results.slice(0, 3);
  for (let j = 0; j < results.length; j++) {
    const result = results[j];
    formattedResearch += (j + 1) + '. ' + result.title + '\n';
    formattedResearch += '   URL: ' + result.url + '\n';
    formattedResearch += '   ' + result.content.substring(0, 300) + '...\n\n';
  }
}

// Gebe alle Daten zurück plus formatted research
return {
  'Investor Name': $json['Investor Name'],
  'URL': $json.URL,
  'Investor Type': $json['Investor Type'],
  'research_data': researchData,
  'formatted_research': formattedResearch
};
