// Alle Daten aus vorherigen Nodes sammeln
const config = $('Webhook Daten extrahieren').item.json;
const videoDetails = $('YouTube Video Details').item.json;
const transcriptData = $('Transkript extrahieren').item.json;

// Transkript extrahieren und bereinigen
let rawTranscript = transcriptData.transcript || transcriptData.text || '';
if (Array.isArray(transcriptData)) {
  rawTranscript = transcriptData.map(item => item.text || item.content || '').join(' ');
}

// Text bereinigen und formatieren
const cleanTranscript = rawTranscript
  .replace(/\[.*?\]/g, '')           // [Musik], [Applaus] etc.
  .replace(/\(.*?\)/g, '')           // (inaudible) etc.
  .replace(/\s+/g, ' ')              // Mehrfache Leerzeichen
  .replace(/\n+/g, '\n\n')           // Absätze formatieren
  .trim();

// Video-Dauer aus ISO 8601 Format parsen (PT4M13S -> 4:13)
function parseDuration(duration) {
  if (!duration) return 'Unknown';
  const match = duration.match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
  if (!match) return duration;

  const hours = parseInt(match[1] || 0);
  const minutes = parseInt(match[2] || 0);
  const seconds = parseInt(match[3] || 0);

  if (hours > 0) {
    return `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  } else {
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  }
}

// Tags aus Video-Titel und Beschreibung extrahieren
function extractTags(title, description) {
  const tags = [];
  const text = (title + ' ' + description).toLowerCase();

  // AI/KI-bezogene Keywords
  if (text.match(/(ai|artificial intelligence|chatgpt|gpt|claude|llm|machine learning|automation)/)) {
    tags.push('AI');
  }

  // Marketing Keywords
  if (text.match(/(marketing|sales|business|growth|strategy|revenue)/)) {
    tags.push('Marketing');
  }

  // Tutorial Keywords
  if (text.match(/(tutorial|how to|guide|step by step|walkthrough|setup)/)) {
    tags.push('Tutorial');
  }

  // Tool Keywords
  if (text.match(/(tool|software|app|platform|api|integration)/)) {
    tags.push('Tools');
  }

  return tags.length > 0 ? tags : ['AI']; // Default fallback
}

return {
  // Video Grunddaten
  videoId: config.videoId,
  title: videoDetails.snippet?.title || 'Unbekannt',
  channelTitle: videoDetails.snippet?.channelTitle || 'Unbekannt',
  authorName: config.authorName || 'Unbekannt',
  publishedAt: videoDetails.snippet?.publishedAt || new Date().toISOString(),
  description: videoDetails.snippet?.description || '',

  // Statistiken
  viewCount: parseInt(videoDetails.statistics?.viewCount || 0),
  likeCount: parseInt(videoDetails.statistics?.likeCount || 0),
  duration: parseDuration(videoDetails.contentDetails?.duration),

  // URLs
  videoUrl: `https://youtube.com/watch?v=${config.videoId}`,
  thumbnailUrl: videoDetails.snippet?.thumbnails?.high?.url || '',

  // Transkript
  cleanTranscript: cleanTranscript,
  transcriptLength: cleanTranscript.length,
  wordCount: cleanTranscript.split(' ').length,
  estimatedReadingTime: Math.ceil(cleanTranscript.split(' ').length / 200),

  // Auto-Tags
  autoTags: extractTags(videoDetails.snippet?.title || '', videoDetails.snippet?.description || '')
};

